import React from 'react'
import { useState } from 'react'


const Rating = () => {

    const [rating,setRating] = useState(0)

    const incrementRating = () => {
        if (rating < 5) {
          setRating(rating + 1)
        }
      }
    
    const decrementRating = () => {
        if (rating > 0) {
          setRating(rating - 1)
        }
      }

    return (
        <div>
            <div> <h1>Rating  : {rating.toFixed()} </h1></div>
            <div>
                <button className='btn btn-success' onClick={incrementRating}> + </button>
                <span> </span>
                <button className='btn btn-success' onClick={decrementRating}> - </button>
            </div>
        </div>
    )
}

export default Rating
